CREATE
DATABASE IF NOT EXISTS teamtest;
USE
teamtest;

-- 移除既有 NEWS_LIKE 資料表（若存在）
DROP TABLE IF EXISTS NEWS_LIKE;

-- 建立 NEWS_LIKE 資料表
CREATE TABLE NEWS_LIKE
(
    NLIKE_NO     INT AUTO_INCREMENT NOT NULL,
    NLIKE_STATUS CHAR(1)  NOT NULL COMMENT '喜愛:1 正常:0 不喜愛:2',
    MEM_NO       INT      NOT NULL,
    NLIKE_UPDATE DATETIME NOT NULL ON UPDATE CURRENT_TIMESTAMP,
    NLIKE_CRDATE DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '自動填入插入當下時間',
    NCOM_NO      INT      NOT NULL,
    CONSTRAINT NEWS_LIKE_PK PRIMARY KEY (NLIKE_NO),
    CONSTRAINT NEWS_LIKE_FK_MEMBER FOREIGN KEY (MEM_NO) REFERENCES MEMBER (MEM_NO),
    CONSTRAINT NEWS_LIKE_FK_COMMENT FOREIGN KEY (NCOM_NO) REFERENCES NEWS_COMMENTS (NCOM_NO)
) AUTO_INCREMENT = 1;
